import styled from "styled-components";
import tw from "twin.macro";

export const ToolTipContainer = styled.div`
  ${tw`relative`}
`;
export const Item = styled.div`
  ${tw` `}
`;
export const Content = styled.div`
  ${tw`absolute min-w-[150px] min-h-[50px] rounded-lg -top-4 left-full text-secondary-1 bg-primary flex items-center justify-center`}
  &:before {
    content: "";
    ${tw`border-[5px] border-t-primary border-l-transparent border-r-transparent border-b-transparent left-0 top-full`}
  }
`;
