import styled from "styled-components";
import tw from "twin.macro";

export const AboutMeContainer = styled.div`
  ${tw`mt-14 w-full`}
`;
export const Description = styled.div`
  ${tw`mt-7 font-normal text-lg text-center text-primary-2`}
`;
export const Line = styled.div`
  ${tw`flex justify-center items-center w-full`}
`;
export const Other = {
  Container: styled.div`
    ${tw`flex relative flex-col justify-center items-start w-full`}
  `,
  Title: styled.h3`
    ${tw`text-xxl font-bold  text-primary `}
  `,
  Desc: styled.p`
    ${tw`mt-1 text-md font-normal  text-primary `}
  `,
  Desc: styled.p`
    ${tw`mt-1 text-md font-normal  text-primary `}
  `,
};
