import styled from "styled-components";
import tw from "twin.macro";

export const HeaderContainer = styled.div`
  ${tw`h-6 phone:px-6 px-2 gap-x-2 py-1 flex justify-end bg-primary`}
`;
export const MenuItem = styled.div`
  ${tw`py-0.5 overflow-hidden duration-300 cursor-pointer relative  px-1.5 text-lg  text-secondary-1 font-bold`}
  &:before {
    content: "";
    ${tw`absolute bottom-0 left-0 w-full h-[2px] bg-secondary-1`};
    transform: scale(0);
    transition: 1s;
    transform-origin: right center;
  }
  &:hover {
    &:before {
      transition: 1s;
      transform: scale(1);
      transform-origin: center left;
    }
  }
`;
export const Item = styled.p`
  ${tw`z-30 relative text-secondary-1`}
`;
