import styled from "styled-components";
import tw from "twin.macro";

export const SkillTemplateContainer = styled.div`
  ${tw`w-full flex flex-col gap-y-7`}
`;

export const Title = styled.h3`
  ${tw`text-4xl uppercase text-primary font-bold tracking-[10px]`}
`;
export const List = styled.div`
  ${tw`grid grid-cols-2 gap-x-10 phone:grid-cols-4`}
`;
export const Item = styled.div`
  ${tw`col-span-1 flex flex-col gap-1 justify-center items-start`}
`;
export const ItemIcon = styled.div`
  ${tw`flex items-start justify-center h-10`}
  svg {
    ${tw`w-full object-cover`}
  }
`;
export const ItemName = styled.p`
  ${tw`text-center uppercase text-3xl font-normal py-0.5`}
`;
