import { ToolTipContainer, Item, Content } from "./styles";

interface IToolTipProps {
  item: JSX.Element;
  className?: string;
  renderContent: () => void;
}
//Parent must use position:relative, this component used position:absolute
const ToolTip: React.FC<IToolTipProps> = props => {
  const { children, item, className = "", renderContent } = props;
  return (
    <ToolTipContainer className={className}>
      <Item>{item}</Item>
      <Content>{renderContent()}</Content>
    </ToolTipContainer>
  );
};

export default ToolTip;
