import styled from "styled-components";
import tw from "twin.macro";

export const StartScreenContainer = styled.div<{ open: boolean }>`
  ${tw`absolute flex items-center justify-center left-0 transform duration-1000 top-0 fixed bottom-0 bg-primary right-0`}
  ${({ open }) => !open && "animation:mix 1s ease-in-out;"}
  @keyframes mix {
    0% {
      transform: translate(0) rotate(0) scale(1);
    }
    25% {
      transform: translate(-25%, -25%) rotate(-45deg) scale(0.75);
    }
    50% {
      transform: translate(-50%, -50%) rotate(-90deg) scale(0.5);
    }
    75% {
      transform: translate(-75%, -75%) rotate(-180deg) scale(0.25);
    }
    100% {
      transform: translate(-100%, -100%) rotate(360deg) scale(0);
    }
  }
`;
