import styled from "styled-components";
import tw from "twin.macro";

export const HomeContainer = styled.div<{ open: boolean }>`
  ${tw`w-full transform duration-1000`}
  ${({ open }) =>
    !open ? "transform: translateX(0);" : "transform: translateX(100%);"}
`;

export const Typing = {
  Container: styled.div`
    ${tw`w-full h-full max-w-40`}
  `,
  Name: styled.h1`
    ${tw`text-secondary-1 text-xxl`}
  `,
  Text: styled.h2`
    ${tw`text-secondary-1 mt-1 text-xl`}
  `,
};
