import styled from "styled-components";
import tw from "twin.macro";

export const BannerContainer = styled.div`
  ${tw`overflow-hidden relative bg-secondary-2 w-screen h-screen flex  `}
  &:before {
    content: "";
    ${tw`absolute left-0 top-0 bottom-0 right-0 bg-primary`}
    transform:rotate(9.67deg) scale(1.5) translateX(50%);
  }
`;
export const Avatar = {
  Container: styled.div`
    ${tw`w-1/2 flex items-center justify-start pl-4`}
  `,
  Image: styled.img`
    ${tw`h-full object-cover`}
  `,
};
export const Information = {
  Container: styled.div`
    ${tw`w-1/2 flex flex-col items-center justify-start pl-4`}
  `,
  Greeting: styled.h3`
    ${tw`text-6xl text-primary font-bold`}
  `,
};
