import styled from "styled-components";
import tw from "twin.macro";

export const StartScreenContainer = styled.div<{ open: boolean }>`
  ${tw`absolute flex items-center justify-center left-0 transform duration-700 top-0 fixed bottom-0 bg-primary right-0`}
  ${({ open }) =>
    open ? `transform:translateX(0);` : "animation: mixAll  0.5s ease-in-out;"}
    @keyframe mixAll {
    0% {
      transform: translate(0, 0) rotate(0deg) scale(1);
    }

    50% {
      transform: translate(-50%, -50%) rotate(180deg) scale(0.5);
    }

    100% {
      transform: translate(-100%, -100%) rotate(360deg) scale(0);
    }
  }
`;
