import styled from "styled-components";
import tw from "twin.macro";

export const HeaderContainer = styled.div`
  ${tw`h-6 w-full flex justify-end bg-primary`}
`;
export const MenuItem = styled.div`
  ${tw`py-0.5 cursor-pointer relative rounded-lg px-1.5 text-lg text-secondary-1 font-bold`}
  &:before {
    content: "";
    ${tw`absolute top-0 left-0 right-0 bottom-0 bg-secondary-1 z-20`}
  }
`;
export const Item = styled.p`
  ${tw`z-30 relative`}
`;
